-- ----------------------------
-- Table structure for blog_article
-- ----------------------------
DROP TABLE IF EXISTS `blog_article`;
CREATE TABLE `blog_article` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `article_title` varchar(20) DEFAULT NULL,
  `article_content` mediumtext,
  `article_fbtime` datetime DEFAULT NULL,
  `article_faceimg` varchar(100) DEFAULT NULL,
  `article_like` int(11) DEFAULT NULL,
  `article_timeaxis` varchar(10) DEFAULT NULL,
  `article_remarknum` int(11) DEFAULT NULL,
  `userId` int(11) DEFAULT NULL,
  `menuId` int(11) DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `fk_content_menu` (`menuId`),
  KEY `fk_content_user` (`userId`),
  CONSTRAINT `fk_content_menu` FOREIGN KEY (`menuId`) REFERENCES `blog_menu` (`id`) ON DELETE SET NULL,
  CONSTRAINT `fk_content_user` FOREIGN KEY (`userId`) REFERENCES `blog_user` (`id`) ON DELETE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=40 DEFAULT CHARSET=gbk;

